import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);

  const calculateAttendance = () => {
    if (totalClasses <= 0) {
      alert("‚ùå Total classes must be greater than 0.");
      return;
    }
    if (attendedClasses < 0) {
      alert("‚ùå Attended classes cannot be negative.");
      return;
    }
    if (attendedClasses > totalClasses) {
      alert("‚ùå Attended classes cannot exceed total classes.");
      return;
    }

    const percentage = ((attendedClasses / totalClasses) * 100).toFixed(2);
    setResult(percentage);

    setChartData([
      { name: "Attended", value: attendedClasses },
      { name: "Missed", value: totalClasses - attendedClasses },
    ]);
  };

  const resetAll = () => {
    setTotalClasses(0);
    setAttendedClasses(0);
    setResult(null);
    setChartData([]);
  };

  return (
    <div className="app-container">
      <div className="attendance-card">
        <h2 className="title">üìä Attendance Tracker</h2>

        <div className="input-section">
          <label>Total Classes:</label>
          <input
            type="number"
            value={totalClasses}
            onChange={(e) => setTotalClasses(Number(e.target.value))}
          />

          <label>Attended Classes:</label>
          <input
            type="number"
            value={attendedClasses}
            onChange={(e) => setAttendedClasses(Number(e.target.value))}
          />
        </div>

        <div className="button-group">
          <button onClick={calculateAttendance}>Calculate</button>
          <button className="reset-btn" onClick={resetAll}>
            Reset
          </button>
        </div>

        {result && (
          <div className="result-section">
            <h3>Your Attendance: {result}%</h3>
            <p>Total Classes: {totalClasses}</p>
            <div className="chart-container">
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={chartData}>
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#4f46e5" radius={[10, 10, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
            <p className="status-text">
              {result >= 75
                ? "‚úÖ Good! You‚Äôre maintaining attendance."
                : "‚ö†Ô∏è Warning! Your attendance is below 75%."}
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
