import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

export default function App() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);

  const calculateAttendance = () => {
    if (totalClasses === 0) {
      setResult("‚ö†Ô∏è Total classes must be greater than 0");
      return;
    }

    let attendancePercentage = (attendedClasses / totalClasses) * 100;
    let requiredClasses = 0;
    let maxLeaves = 0;

    if (attendancePercentage < 75) {
      requiredClasses = Math.ceil(
        (0.75 * totalClasses - attendedClasses) / 0.25
      );
      setResult(
        `üìö Your attendance is ${attendancePercentage.toFixed(
          2
        )}%. You need to attend ${requiredClasses} more classes to reach 75%.`
      );
    } else {
      maxLeaves = Math.floor((attendedClasses - 0.75 * totalClasses) / 0.75);
      setResult(
        `‚úÖ Your attendance is ${attendancePercentage.toFixed(
          2
        )}%. You can take ${maxLeaves} more leave(s) and still maintain 75%.`
      );
    }

    setChartData([
      { name: "Total Classes", value: totalClasses },
      { name: "Attended Classes", value: attendedClasses },
      { name: "Required for 75%", value: requiredClasses },
      { name: "Max Leaves", value: maxLeaves },
    ]);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-400 p-6">
      <div className="bg-white/20 backdrop-blur-xl shadow-2xl rounded-3xl p-8 w-full max-w-md text-center border border-white/30">
        <h2 className="text-4xl font-extrabold text-white mb-6 drop-shadow-md">
          üéØ Attendance Tracker
        </h2>

        <div className="space-y-4">
          <input
            type="number"
            placeholder="Total Classes Held"
            className="w-full p-3 rounded-xl border-2 border-white/50 bg-white/30 text-white placeholder-white/70 text-lg focus:outline-none focus:ring-4 focus:ring-white/60 focus:border-transparent"
            onChange={(e) => setTotalClasses(Number(e.target.value))}
          />

          <input
            type="number"
            placeholder="Classes Attended"
            className="w-full p-3 rounded-xl border-2 border-white/50 bg-white/30 text-white placeholder-white/70 text-lg focus:outline-none focus:ring-4 focus:ring-white/60 focus:border-transparent"
            onChange={(e) => setAttendedClasses(Number(e.target.value))}
          />

          <button
            className="w-full bg-white text-indigo-700 font-semibold text-lg p-3 rounded-xl hover:bg-indigo-100 hover:scale-105 transition-all duration-200 shadow-lg"
            onClick={calculateAttendance}
          >
            Calculate Attendance
          </button>
        </div>

        {result && (
          <div className="mt-6 bg-white/30 text-white p-4 rounded-xl shadow-inner font-medium leading-relaxed">
            {result}
          </div>
        )}
      </div>

      {chartData.length > 0 && (
        <div className="mt-10 bg-white/20 backdrop-blur-xl shadow-xl rounded-3xl p-6 w-full max-w-lg border border-white/30 text-white">
          <h3 className="text-xl font-semibold mb-4 text-center">
            üìà Attendance Overview
          </h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={chartData}>
              <XAxis dataKey="name" stroke="#fff" />
              <YAxis stroke="#fff" />
              <Tooltip
                contentStyle={{
                  backgroundColor: "rgba(255,255,255,0.9)",
                  borderRadius: "10px",
                }}
              />
              <Bar dataKey="value" fill="#ffffff" radius={[10, 10, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}
