import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  ReferenceLine,
  Legend,
} from "recharts";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);

  const handleCalculate = () => {
    if (totalClasses <= 0) {
      alert("Total classes must be greater than 0");
      return;
    }
    if (attendedClasses < 0 || attendedClasses > totalClasses) {
      alert("Attended classes must be between 0 and total classes");
      return;
    }

    const percentage = ((attendedClasses / totalClasses) * 100).toFixed(2);
    const missed = totalClasses - attendedClasses;

    setResult({
      percentage,
      missed,
      status: percentage >= 75 ? "Eligible ✅" : "Not Eligible ❌",
    });
  };

  const chartData = [
    {
      name: "Attendance",
      Attended: attendedClasses,
      Missed: totalClasses - attendedClasses,
    },
  ];

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-indigo-500 to-indigo-300 font-poppins p-4">
      <div className="bg-white/90 rounded-2xl shadow-2xl p-6 w-full max-w-md text-center">
        <h1 className="text-3xl font-bold text-indigo-600 mb-6">
          Attendance Tracker
        </h1>

        {/* Input Fields */}
        <div className="space-y-4">
          <input
            type="number"
            placeholder="Total Classes"
            value={totalClasses}
            onChange={(e) => setTotalClasses(Number(e.target.value))}
            className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-400 outline-none"
          />
          <input
            type="number"
            placeholder="Attended Classes"
            value={attendedClasses}
            onChange={(e) => setAttendedClasses(Number(e.target.value))}
            className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-400 outline-none"
          />
          <button
            onClick={handleCalculate}
            className="w-full bg-indigo-600 text-white py-2 rounded-lg font-semibold hover:bg-indigo-700 transition"
          >
            Calculate Attendance
          </button>
        </div>

        {/* Result Section */}
        {result && (
          <div className="mt-6">
            <h2 className="text-xl font-semibold mb-2">
              Attendance: {result.percentage}%
            </h2>
            <p className="text-gray-600">Total Classes: {totalClasses}</p>
            <p className="text-gray-600">Attended: {attendedClasses}</p>
            <p className="text-gray-600 mb-4">Missed: {result.missed}</p>
            <p
              className={`text-lg font-bold ${
                result.status.includes("Eligible")
                  ? "text-green-600"
                  : "text-red-500"
              }`}
            >
              {result.status}
            </p>
          </div>
        )}

        {/* Chart */}
        {result && (
          <div className="mt-8 h-64">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={chartData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <ReferenceLine
                  y={(totalClasses * 0.75).toFixed(2)}
                  stroke="red"
                  label="75% Required"
                />
                <Bar dataKey="Attended" fill="#34d399" barSize={60} />
                <Bar dataKey="Missed" fill="#f87171" barSize={60} />
              </BarChart>
            </ResponsiveContainer>
          </div>
        )}
      </div>
    </div>
  );
}
