import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

export default function App() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);

  const calculateAttendance = () => {
    if (totalClasses === 0) {
      setResult("‚ö†Ô∏è Total classes must be greater than 0");
      return;
    }

    let attendancePercentage = (attendedClasses / totalClasses) * 100;
    let requiredClasses = 0;
    let maxLeaves = 0;

    if (attendancePercentage < 75) {
      requiredClasses = Math.ceil(
        (0.75 * totalClasses - attendedClasses) / 0.25
      );
      setResult(
        `üìö Your attendance is ${attendancePercentage.toFixed(
          2
        )}%. You need to attend ${requiredClasses} more classes to reach 75%.`
      );
    } else {
      maxLeaves = Math.floor((attendedClasses - 0.75 * totalClasses) / 0.75);
      setResult(
        `‚úÖ Your attendance is ${attendancePercentage.toFixed(
          2
        )}%. You can take ${maxLeaves} more leave(s) and still maintain 75%.`
      );
    }

    setChartData([
      { name: "Total Classes", value: totalClasses },
      { name: "Attended Classes", value: attendedClasses },
      { name: "Required for 75%", value: requiredClasses },
      { name: "Max Leaves", value: maxLeaves },
    ]);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-md border border-gray-100">
        <h2 className="text-3xl font-extrabold text-center mb-6 text-indigo-600">
          Attendance Tracker üìä
        </h2>

        <div className="space-y-3">
          <input
            type="number"
            placeholder="Total Classes Held"
            className="w-full p-3 border-2 border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
            onChange={(e) => setTotalClasses(Number(e.target.value))}
          />

          <input
            type="number"
            placeholder="Classes Attended"
            className="w-full p-3 border-2 border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-400 transition"
            onChange={(e) => setAttendedClasses(Number(e.target.value))}
          />

          <button
            className="w-full bg-indigo-600 text-white font-semibold p-3 rounded-lg hover:bg-indigo-700 hover:shadow-lg transition-transform transform hover:-translate-y-1"
            onClick={calculateAttendance}
          >
            Calculate Attendance
          </button>
        </div>

        {result && (
          <div className="mt-6 bg-indigo-50 border border-indigo-200 text-indigo-800 p-4 rounded-lg text-center shadow-sm">
            {result}
          </div>
        )}
      </div>

      {chartData.length > 0 && (
        <div className="mt-10 bg-white shadow-md rounded-2xl p-6 w-full max-w-lg border border-gray-100">
          <h3 className="text-lg font-semibold text-gray-700 mb-4 text-center">
            Attendance Overview
          </h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={chartData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#6366f1" radius={[10, 10, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}
