import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);

  const calculateAttendance = () => {
    // ‚úÖ Input validation
    if (totalClasses <= 0) {
      setResult("‚ö†Ô∏è Total classes must be greater than 0");
      return;
    }
    if (attendedClasses < 0) {
      setResult("‚ö†Ô∏è Attended classes cannot be negative");
      return;
    }
    if (attendedClasses > totalClasses) {
      setResult("‚ö†Ô∏è Attended classes cannot exceed total classes");
      return;
    }

    // ‚úÖ Core logic (unchanged)
    let attendancePercentage = (attendedClasses / totalClasses) * 100;
    let requiredClasses = 0;
    let maxLeaves = 0;
    let message = "";

    if (attendancePercentage < 75) {
      requiredClasses = Math.ceil(
        (0.75 * totalClasses - attendedClasses) / 0.25
      );
      message = `‚ö†Ô∏è Your attendance is ${attendancePercentage.toFixed(
        2
      )}%. You need to attend ${requiredClasses} more class${
        requiredClasses !== 1 ? "es" : ""
      } to reach 75%.`;
    } else {
      maxLeaves = Math.floor((attendedClasses - 0.75 * totalClasses) / 0.75);
      message = `‚úÖ Your attendance is ${attendancePercentage.toFixed(
        2
      )}%. You can take ${maxLeaves} more leave${
        maxLeaves !== 1 ? "s" : ""
      } and still maintain 75%.`;
    }

    setResult(message);
    setChartData([
      { name: "Total Classes", value: totalClasses },
      { name: "Attended", value: attendedClasses },
      { name: "Required for 75%", value: requiredClasses },
      { name: "Max Leaves", value: maxLeaves },
    ]);
  };

  const resetData = () => {
    setTotalClasses(0);
    setAttendedClasses(0);
    setResult(null);
    setChartData([]);
  };

  return (
    <div className="app-container">
      <div className="card">
        <h1 className="title">üéì Attendance Tracker</h1>
        <input
          type="number"
          placeholder="Total Classes Held"
          className="input-box"
          value={totalClasses}
          onChange={(e) => setTotalClasses(Number(e.target.value))}
        />
        <input
          type="number"
          placeholder="Classes Attended"
          className="input-box"
          value={attendedClasses}
          onChange={(e) => setAttendedClasses(Number(e.target.value))}
        />

        <div className="button-group">
          <button className="btn" onClick={calculateAttendance}>
            Calculate
          </button>
          <button className="btn reset" onClick={resetData}>
            Reset
          </button>
        </div>

        {result && <div className="result-box">{result}</div>}
      </div>

      {chartData.length > 0 && (
        <div className="chart-section">
          <ResponsiveContainer width={400} height={300}>
            <BarChart data={chartData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#6366F1" radius={[10, 10, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}
