import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);

  const calculateAttendance = () => {
    if (totalClasses === 0) {
      setResult("‚ö†Ô∏è Total classes must be greater than 0");
      return;
    }

    const attendancePercentage = (attendedClasses / totalClasses) * 100;
    let requiredClasses = 0;
    let maxLeaves = 0;

    if (attendancePercentage < 75) {
      requiredClasses = Math.ceil(
        (0.75 * totalClasses - attendedClasses) / 0.25
      );
      setResult(
        `üìâ Your attendance is ${attendancePercentage.toFixed(
          2
        )}%. You need to attend ${requiredClasses} more class(es) to reach 75%.`
      );
    } else {
      maxLeaves = Math.floor((attendedClasses - 0.75 * totalClasses) / 0.75);
      setResult(
        `‚úÖ Your attendance is ${attendancePercentage.toFixed(
          2
        )}%. You can take ${maxLeaves} more leave(s) and still maintain 75%.`
      );
    }

    setChartData([
      { name: "Total Classes", value: totalClasses },
      { name: "Attended Classes", value: attendedClasses },
      { name: "Required for 75%", value: requiredClasses },
      { name: "Max Leaves", value: maxLeaves },
    ]);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <div className="bg-white shadow-lg rounded-xl p-6 w-96">
        <h2 className="text-2xl font-bold text-center mb-4 text-blue-600">
          Attendance Tracker
        </h2>

        <input
          type="number"
          placeholder="Total Classes Held"
          className="w-full p-2 border rounded mb-2"
          onChange={(e) => setTotalClasses(Number(e.target.value))}
        />

        <input
          type="number"
          placeholder="Classes Attended"
          className="w-full p-2 border rounded mb-2"
          onChange={(e) => setAttendedClasses(Number(e.target.value))}
        />

        <button
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition"
          onClick={calculateAttendance}
        >
          Calculate Attendance
        </button>

        {result && (
          <div className="mt-4 p-3 bg-blue-100 text-blue-700 rounded text-center text-sm">
            {result}
          </div>
        )}
      </div>

      {chartData.length > 0 && (
        <div className="mt-8 w-full flex justify-center">
          <ResponsiveContainer width={400} height={300}>
            <BarChart data={chartData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="value" fill="#3b82f6" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}
