import React, { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState("");
  const [attendedClasses, setAttendedClasses] = useState("");
  const [result, setResult] = useState(null);

  const handleCalculate = () => {
    const total = Number(totalClasses);
    const attended = Number(attendedClasses);

    if (total <= 0 || attended < 0 || attended > total) {
      setResult("âš ï¸ Please enter valid numbers.");
      return;
    }

    const percentage = ((attended / total) * 100).toFixed(2);

    let message = "";
    if (percentage >= 75) {
      message = `âœ… You are safe! Attendance is ${percentage}%.`;
    } else {
      const required = Math.ceil(0.75 * total - attended);
      message = `âŒ You need to attend ${required} more class${
        required > 1 ? "es" : ""
      } to reach 75%.`;
    }

    setResult({ percentage, message });
  };

  const chartData = [
    {
      name: "Attendance",
      Attended: Number(attendedClasses) || 0,
      Missed:
        Number(totalClasses) - Number(attendedClasses) > 0
          ? Number(totalClasses) - Number(attendedClasses)
          : 0,
    },
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center p-6">
      <h1 className="text-3xl font-bold mb-6 text-blue-400">
        ðŸŽ“ Attendance Tracker
      </h1>

      <div className="bg-gray-800 p-6 rounded-2xl shadow-lg w-full max-w-md">
        <label className="block mb-3 text-lg">
          Total Classes:
          <input
            type="number"
            value={totalClasses}
            onChange={(e) => setTotalClasses(e.target.value)}
            className="mt-1 w-full p-2 rounded bg-gray-700 text-white outline-none focus:ring-2 focus:ring-blue-400"
          />
        </label>

        <label className="block mb-3 text-lg">
          Attended Classes:
          <input
            type="number"
            value={attendedClasses}
            onChange={(e) => setAttendedClasses(e.target.value)}
            className="mt-1 w-full p-2 rounded bg-gray-700 text-white outline-none focus:ring-2 focus:ring-blue-400"
          />
        </label>

        <button
          onClick={handleCalculate}
          className="w-full mt-3 bg-blue-500 hover:bg-blue-600 py-2 rounded font-semibold transition-all"
        >
          Calculate Attendance
        </button>

        {result && (
          <div className="mt-4 p-3 rounded bg-gray-700 text-center">
            <p className="text-lg">{result.message}</p>
            {typeof result.percentage !== "undefined" && (
              <p className="text-blue-400 text-xl mt-2 font-bold">
                {result.percentage}%
              </p>
            )}
          </div>
        )}
      </div>

      {/* Attendance Bar Chart */}
      <div className="w-full max-w-md mt-8 bg-gray-800 p-4 rounded-2xl shadow-lg">
        <h2 className="text-center text-lg font-semibold mb-3 text-blue-400">
          Attendance Overview
        </h2>
        <ResponsiveContainer width="100%" height={250}>
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis dataKey="name" stroke="#ccc" />
            <YAxis stroke="#ccc" />
            <Tooltip
              contentStyle={{
                backgroundColor: "#333",
                borderRadius: "10px",
                border: "none",
              }}
            />
            <Bar dataKey="Attended" fill="#3b82f6" />
            <Bar dataKey="Missed" fill="#ef4444" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
