import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
  LabelList,
} from "recharts";
import "./App.css";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);

  const calculateAttendance = () => {
    if (totalClasses <= 0) {
      alert("‚ùå Total classes must be greater than 0.");
      return;
    }
    if (attendedClasses < 0) {
      alert("‚ùå Attended classes cannot be negative.");
      return;
    }
    if (attendedClasses > totalClasses) {
      alert("‚ùå Attended classes cannot exceed total classes.");
      return;
    }

    const percentage = ((attendedClasses / totalClasses) * 100).toFixed(2);
    setResult(percentage);

    setChartData([
      { name: "Attended", value: attendedClasses },
      { name: "Missed", value: totalClasses - attendedClasses },
    ]);
  };

  const resetAll = () => {
    setTotalClasses(0);
    setAttendedClasses(0);
    setResult(null);
    setChartData([]);
  };

  return (
    <div className="app-container">
      <div className="card">
        <h2 className="title">üìò Attendance Tracker</h2>

        <input
          className="input-box"
          type="number"
          placeholder="Enter total classes"
          value={totalClasses}
          onChange={(e) => setTotalClasses(Number(e.target.value))}
        />

        <input
          className="input-box"
          type="number"
          placeholder="Enter attended classes"
          value={attendedClasses}
          onChange={(e) => setAttendedClasses(Number(e.target.value))}
        />

        <div className="button-group">
          <button className="btn" onClick={calculateAttendance}>
            Calculate
          </button>
          <button className="btn reset" onClick={resetAll}>
            Reset
          </button>
        </div>

        {result && (
          <>
            <div className="result-box">
              <p>üìÖ Total Classes: {totalClasses}</p>
              <p>‚úÖ Attended: {attendedClasses}</p>
              <p>üìä Attendance: {result}%</p>
              <p>
                {result >= 75
                  ? "üéâ Great! You‚Äôre maintaining good attendance."
                  : "‚ö†Ô∏è Warning! Attendance below 75%."}
              </p>
            </div>

            <div className="chart-section">
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={chartData} barSize={80}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#4f46e5" radius={[10, 10, 0, 0]}>
                    <LabelList dataKey="value" position="top" />
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
