import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState(0);
  const [attendedClasses, setAttendedClasses] = useState(0);
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);
  const [message, setMessage] = useState("");

  const calculateAttendance = () => {
    // ‚úÖ Input validation
    if (totalClasses <= 0) {
      alert("‚ö†Ô∏è Total classes must be greater than zero!");
      return;
    }
    if (attendedClasses < 0) {
      alert("‚ö†Ô∏è Attended classes cannot be negative!");
      return;
    }
    if (attendedClasses > totalClasses) {
      alert("‚ö†Ô∏è Attended classes cannot exceed total classes!");
      return;
    }

    const percentage = ((attendedClasses / totalClasses) * 100).toFixed(2);
    setResult(percentage);

    const data = [
      { name: "Attended", value: attendedClasses },
      { name: "Missed", value: totalClasses - attendedClasses },
    ];
    setChartData(data);

    // ‚úÖ Maintain 75% logic
    let msg = "";
    if (percentage >= 75) {
      const canMiss = Math.floor(attendedClasses / 0.75 - totalClasses);
      msg = `‚úÖ Great! You are maintaining 75%. You can miss ${canMiss} more class${
        canMiss !== 1 ? "es" : ""
      }.`;
    } else {
      const needToAttend = Math.ceil(
        (0.75 * totalClasses - attendedClasses) / 0.25
      );
      msg = `‚ö†Ô∏è You need to attend ${needToAttend} more class${
        needToAttend !== 1 ? "es" : ""
      } to reach 75%.`;
    }
    setMessage(msg);
  };

  return (
    <div className="app-container">
      <div className="attendance-card">
        <h1 className="title">üéì Attendance Tracker</h1>

        <div className="input-section">
          <label>Total Classes:</label>
          <input
            type="number"
            min="0"
            value={totalClasses}
            onChange={(e) => setTotalClasses(Number(e.target.value))}
          />

          <label>Attended Classes:</label>
          <input
            type="number"
            min="0"
            value={attendedClasses}
            onChange={(e) => setAttendedClasses(Number(e.target.value))}
          />

          <button onClick={calculateAttendance}>Calculate</button>
        </div>

        {result !== null && (
          <div className="result-section">
            <h2>üìä Attendance: {result}%</h2>
            <div className="chart-container">
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={chartData}>
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#4F46E5" radius={[10, 10, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
            <p className="status-text">{message}</p>
          </div>
        )}
      </div>
    </div>
  );
}
