import { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import "./App.css";

export default function AttendanceTracker() {
  const [totalClasses, setTotalClasses] = useState("");
  const [attendedClasses, setAttendedClasses] = useState("");
  const [result, setResult] = useState(null);
  const [chartData, setChartData] = useState([]);
  const [message, setMessage] = useState("");

  const calculateAttendance = () => {
    const total = Number(totalClasses);
    const attended = Number(attendedClasses);

    // âœ… Validation
    if (total <= 0) {
      alert("âš ï¸ Total classes must be greater than zero!");
      return;
    }
    if (attended < 0) {
      alert("âš ï¸ Attended classes cannot be negative!");
      return;
    }
    if (attended > total) {
      alert("âš ï¸ Attended classes cannot exceed total classes!");
      return;
    }

    const percentage = ((attended / total) * 100).toFixed(2);
    setResult(percentage);

    // âœ… Bar chart data
    const data = [
      { name: "Attended", value: attended },
      { name: "Missed", value: total - attended },
    ];
    setChartData(data);

    // âœ… Keep same 75% logic
    let msg = "";
    if (percentage >= 75) {
      const canMiss = Math.floor(attended / 0.75 - total);
      msg = `âœ… Great! You are maintaining 75%. You can still miss ${canMiss} more class${
        canMiss !== 1 ? "es" : ""
      }.`;
    } else {
      const needToAttend = Math.ceil((0.75 * total - attended) / 0.25);
      msg = `âš ï¸ You need to attend ${needToAttend} more class${
        needToAttend !== 1 ? "es" : ""
      } to reach 75%.`;
    }

    setMessage(msg);
  };

  // ðŸ§¹ Reset Function
  const resetAll = () => {
    setTotalClasses("");
    setAttendedClasses("");
    setResult(null);
    setChartData([]);
    setMessage("");
  };

  return (
    <div className="app-container">
      <div className="attendance-card">
        <h1 className="title">ðŸŽ“ Attendance Tracker</h1>

        <div className="input-section">
          <label>Total Classes:</label>
          <input
            type="number"
            min="0"
            value={totalClasses}
            onChange={(e) => setTotalClasses(e.target.value)}
            placeholder="Enter total classes"
          />

          <label>Attended Classes:</label>
          <input
            type="number"
            min="0"
            value={attendedClasses}
            onChange={(e) => setAttendedClasses(e.target.value)}
            placeholder="Enter attended classes"
          />

          <div className="button-group">
            <button onClick={calculateAttendance}>Calculate</button>
            <button className="reset-btn" onClick={resetAll}>
              Reset
            </button>
          </div>
        </div>

        {result !== null && (
          <div className="result-section">
            <h2>ðŸ“Š Attendance: {result}%</h2>
            <div className="chart-container">
              <ResponsiveContainer width="100%" height={250}>
                <BarChart data={chartData}>
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#4F46E5" radius={[10, 10, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
            <p className="status-text">{message}</p>
          </div>
        )}
      </div>
    </div>
  );
}
